{include file="public/header"}{include file="public/popBox"}    <section id="main-content">        <div class="row">            <div class="col-sm-12">                <div class="ibox float-e-margins">                    <div class="ibox-title">                        <h5>支付宝提现列表</h5>                        <div class="ibox-tools">                            <input type="checkbox" id="checkAll">全选&nbsp;&nbsp;&nbsp;                            <button class="btn btn-primary"><a target="_Blank" id="adopt" style="color: #FFFFFF">通过</a>                            </button>                        </div>                    </div>                    <div class="ibox-content">                        <table class="table table-striped table-bordered table-hover dataTables-example">                            <thead>                            <tr>                                <th>ID</th>                                <th>用户名</th>                                <th>账户类型</th>                                <th>账号</th>                                <th>银行名字</th>                                <th>支行地址</th>                                <th>姓名</th>                                <th>提现金额</th>                                <th>实际到账金额</th>                                <th>订单号</th>                                <th>状态</th>                                <th>打款订单号</th>                                <th>打款类型</th>                                <th>申请时间</th>                                <th>操作</th>                            </tr>                            </thead>                            <tbody>                                                        </tbody>                        </table>                    </div>                </div>            </div>        </div>    </section>{include file="public/foot"}    <script>        $(document).ready(function () {            $('.dataTables-example').dataTable({                'order':[[ 0 ,"desc" ]]            });        });        function del(id){            if(confirm('确定要删除吗?')){                $.ajax({                    'url': '{{url('guoyun/withdraw/del')}}' + '/' + id,                    'type': 'get',                    success: function (res) {                        if (res) {                            swal("删除成功！");                            window.location.reload();                        } else {                            alert(res.message);                        }                    }, error: function () {                        alert('操作失败');                    }                });            }        }        //提现按钮        $('#adopt').on('click', function () {            var allId = '';            var s = $("input[name='allId']");            s.each(function (index, item) {                if (this.checked == true) {                    var id = this.value;                    allId += id + ',';                }            });            if(allId == ""){                alert("请选择");                return;            }else{                $.ajax({                    url:"/guoyun/withdraw/adopt?allId="+allId,                    type:"get",                    success:function(res){                        console.log(res);                        if(res.status == 0){                            location.reload();                        }                    }                });            }//            window.open("/aaddmin/withdraw/adopt?allId=" + allId);        })        //全选        $('#checkAll').on('click', function () {            if (this.checked == true) {                $("input[name='allId']").prop('checked', true);            } else {                $("input[name='allId']").prop('checked', false);            }        })    </script>